function ShowTripsController ($stateParams, Trip) {
  var ctrl = this;

  ctrl.trip = Trip.get({ id: $stateParams.id });

}

angular
  .module('app')
  .controller('ShowTripsController', ShowTripsController);
function TripsCtrl($location, Trip, Auth){
  var ctrl = this;

  ctrl.trip = new Trip();
  ctrl.trips = Trip.query();

  Auth.currentUser()
    .then(function(user){
      ctrl.user = user;
      ctrl.trip.user_id = user.id;
    });

  ctrl.addTrip = function() {
    ctrl.trip.$save(function() {
      $location.path('/home/trips');
    });
  };
}


angular
  .module('app')
  .controller('TripsCtrl', TripsCtrl);
function Trip($resource) {
  var Trip = $resource('/trips/:id.json', {id: '@id'}, {
  update: { method: 'PUT' }
});
  return Trip;
}

angular
  .module('app')
  .factory('Trip', Trip);
